contracts/YourContract.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

contract registerAIGeneratedContent {
    struct Contents {
        string prompt;
        string response; // ‚úÖ New: Store the generated content
        address creator;
        string ipfsID;
        uint256 timeStamp;
    }

    // Mapping from keccak256 hash to content data
    mapping(bytes32 => Contents) public contentByHash;

    // üîç New: Mapping from response text to hash
    mapping(string => bytes32) public hashByResponse;

    event ContentRegistered(
        bytes32 indexed contentHash,
        address indexed creator,
        string prompt,
        string response,
        string ipfsID,
        uint256 timeStamp
    );

    /**
     * Register AI-generated content to the blockchain.
     */
    function registerAIContent(
        bytes32 contentHash,
        string calldata prompt,
        string calldata response,
        string calldata ipfsID
    ) external {
        require(contentByHash[contentHash].timeStamp == 0, "Content already exists");

        contentByHash[contentHash] = Contents({
            prompt: prompt,
            response: response,
            creator: msg.sender,
            ipfsID: ipfsID,
            timeStamp: block.timestamp
        });

        // Map response text to the hash for lookup
        hashByResponse[response] = contentHash;

        emit ContentRegistered(contentHash, msg.sender, prompt, response, ipfsID, block.timestamp);
    }

    /**
     * Retrieve content by its contentHash (from hash-based lookup)
     */
    function getContentByHash(bytes32 contentHash)
        external
        view
        returns (string memory prompt, string memory response, address creator, string memory ipfsID, uint256 timeStamp)
    {
        Contents storage content = contentByHash[contentHash];
        return (content.prompt, content.response, content.creator, content.ipfsID, content.timeStamp);
    }

    /**
     * üîç Lookup content by the AI-generated response text
     */
    function getContentByResponse(string calldata response)
        external
        view
        returns (string memory prompt, string memory responseText, address creator, string memory ipfsID, uint256 timeStamp)
    {
        bytes32 hash = hashByResponse[response];
        Contents storage content = contentByHash[hash];
        require(content.timeStamp != 0, "Content not found");
        return (content.prompt, content.response, content.creator, content.ipfsID, content.timeStamp);
    }

    /**
     * Check if a given contentHash exists
     */
    function contentExists(bytes32 contentHash) external view returns (bool) {
        return contentByHash[contentHash].timeStamp != 0;
    }
}
